mapper.select:
select 
from message
where MESSAGE_ISDELETE='FALSE' AND MESSAGE_RECEIVER_ID=#{receiverId}

select MESSAGE_SENDER_ID, MESSAGE_CONTENTS, MESSAGE_DATE, MESSAGE_TITLE
from message
where message_pk=#{messageNum}

mapper.update:
update message
set MESSAGE_ISDELETE='TRUE'
where message_pk=#{messageNum}

update message
set MESSAGE_ISREAD='TRUE'
where message_pk=#{messageNum}
